import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

const Q1 = () => {
  const [question] = useState("What is your favorite programming language?");
  const videoRef = useRef(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [recording, setRecording] = useState(false);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [recordedVideoUrl, setRecordedVideoUrl] = useState(null);
  const navigate = useNavigate();

  // Prompt user for camera access when component mounts
  useState(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        setMediaStream(stream);
      })
      .catch((err) => console.error("Error accessing media devices: ", err));
  }, []);

  const startRecording = () => {
    if (videoRef.current && !recording) {
      const mediaRecorder = new MediaRecorder(mediaStream);
      const chunks = [];

      mediaRecorder.ondataavailable = (e) => {
        chunks.push(e.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: "video/webm" });
        const url = URL.createObjectURL(blob);
        setRecordedVideoUrl(url);
        setRecordedChunks(chunks);
        setRecording(false);
      };

      mediaRecorder.start();
      setRecording(true);
    }
  };

  const stopRecording = () => {
    if (mediaStream && recording) {
      mediaStream.getTracks().forEach((track) => {
        track.stop();
      });
    }
  };

  const recordAgain = () => {
    setRecordedVideoUrl(null);
    setRecordedChunks([]);
    startRecording();
  };

  const convertToMp4 = async (chunks) => {
    const blob = new Blob(chunks, { type: "video/webm" });
    const url = URL.createObjectURL(blob);
    const video = document.createElement("video");
    video.src = url;
    video.controls = true;
    video.style.display = "none";
    document.body.appendChild(video);

    await video.play();
    const canvas = document.createElement("canvas");
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    canvas.toBlob((blob) => {
      const mp4Url = URL.createObjectURL(blob);
      setRecordedVideoUrl(mp4Url);
    }, "video/mp4");
    video.remove();
  };

  const goToNextQuestion = () => {
    // Navigate to Q2
    navigate("/q2");
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <div className="mb-4">
        {/* Show live preview until recording starts */}
        {!recording && !recordedVideoUrl && (
          <video ref={videoRef} autoPlay muted className="border rounded-lg" />
        )}
        {/* Show recorded video once recording ends */}
        {recordedVideoUrl && (
          <video src={recordedVideoUrl} controls className="border rounded-lg">
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      {!recordedVideoUrl ? (
        <>
          <button
            onClick={recording ? stopRecording : startRecording}
            className={`px-4 py-2 rounded-lg text-white ${
              recording
                ? "bg-red-500 hover:bg-red-600"
                : "bg-green-500 hover:bg-green-600"
            }`}
          >
            {recording ? "Stop Recording" : "Start Recording"}
          </button>
          <button
            onClick={goToNextQuestion}
            className="px-4 py-2 rounded-lg text-white bg-blue-500 hover:bg-blue-600 ml-4"
          >
            Next Question
          </button>
        </>
      ) : (
        <button
          onClick={recordAgain}
          className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
        >
          Record Again
        </button>
      )}
    </div>
  );
};

export default Q1;
