import React, { useState, useRef } from "react";

const Q1 = () => {
  const [question] = useState("What is your favorite programming language?");
  const videoRef = useRef(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [recording, setRecording] = useState(false);
  const [recordedChunks, setRecordedChunks] = useState([]);

  // Prompt user for camera access when component mounts
  useState(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        setMediaStream(stream);
      })
      .catch((err) => console.error("Error accessing media devices: ", err));
  }, []);

  const startRecording = () => {
    if (videoRef.current && !recording) {
      const mediaRecorder = new MediaRecorder(mediaStream);
      const chunks = [];

      mediaRecorder.ondataavailable = (e) => {
        chunks.push(e.data);
      };

      mediaRecorder.onstop = () => {
        setRecordedChunks(chunks);
        setRecording(false);
      };

      mediaRecorder.start();
      setRecording(true);
    }
  };

  const stopRecording = () => {
    if (mediaStream && recording) {
      mediaStream.getTracks().forEach((track) => {
        track.stop();
      });
    }
  };

  const playbackRecording = () => {
    if (recordedChunks.length > 0) {
      const blob = new Blob(recordedChunks, { type: "video/webm" });
      const url = URL.createObjectURL(blob);
      videoRef.current.src = url;
      videoRef.current.play();
    }
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <div className="mb-4">
        {/* Show live preview if recording is not in progress */}
        {!recording && (
          <video ref={videoRef} autoPlay muted className="border rounded-lg" />
        )}
        {/* Show recorded video if available */}
        {recordedChunks.length > 0 && (
          <video ref={videoRef} controls className="border rounded-lg">
            <source
              src={URL.createObjectURL(
                new Blob(recordedChunks, { type: "video/webm" })
              )}
              type="video/webm"
            />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      {!recording ? (
        <button
          onClick={startRecording}
          className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
        >
          Start Recording
        </button>
      ) : (
        <>
          <button
            onClick={stopRecording}
            className="px-4 py-2 rounded-lg text-white bg-red-500 hover:bg-red-600 mr-4"
          >
            Stop Recording
          </button>
          <button
            onClick={playbackRecording}
            className="px-4 py-2 rounded-lg text-white bg-blue-500 hover:bg-blue-600"
          >
            Playback
          </button>
        </>
      )}
    </div>
  );
};

export default Q1;
