import React, { useState, useRef } from "react";

const Q1 = () => {
  const [question] = useState("What is your favorite programming language?");
  const videoRef = useRef(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [recording, setRecording] = useState(false);

  const startRecording = () => {
    if (videoRef.current && !recording) {
      navigator.mediaDevices
        .getUserMedia({ video: true, audio: true })
        .then((stream) => {
          videoRef.current.srcObject = stream;
          setMediaStream(stream);
          setRecording(true);
        })
        .catch((err) => console.error("Error accessing media devices: ", err));
    }
  };

  const stopRecording = () => {
    if (mediaStream && recording) {
      mediaStream.getTracks().forEach((track) => {
        track.stop();
      });
      setMediaStream(null);
      setRecording(false);
    }
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <div className="mb-4">
        <video
          ref={videoRef}
          controls
          className="border rounded-lg"
          autoPlay
          muted
        ></video>
      </div>
      {!recording ? (
        <button
          onClick={startRecording}
          className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
        >
          Start Recording
        </button>
      ) : (
        <button
          onClick={stopRecording}
          className="px-4 py-2 rounded-lg text-white bg-red-500 hover:bg-red-600"
        >
          Stop Recording
        </button>
      )}
    </div>
  );
};

export default Q1;
