import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ReactMediaRecorder } from "react-media-recorder";

const VideoPreview = ({ stream }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  if (!stream) {
    return null;
  }

  return <video ref={videoRef} width={500} height={500} autoPlay controls />;
};

const Q1 = () => {
  const [question] = useState("Q1?");
  const [recordedVideoUrl, setRecordedVideoUrl] = useState(null);
  const [mediaStream, setMediaStream] = useState(null);
  const navigate = useNavigate();

  const handleStopRecording = (blobUrl, blob) => {
    setRecordedVideoUrl(blobUrl);
    uploadVideoToDrive(blob);
  };

  const recordAgain = () => {
    setRecordedVideoUrl(null);
    setMediaStream(null);
  };

  const moveToNextQuestion = () => {
    navigate("/q2");
  };

  const uploadVideoToDrive = async (videoBlob) => {
    try {
      const formData = new FormData();
      formData.append("video", videoBlob, "recorded-video.webm");

      const response = await fetch("http://localhost:3001/api/upload-video", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        console.log("Video uploaded successfully!");
        // Optionally handle response from server
      } else {
        console.error("Failed to upload video:", response.statusText);
      }
    } catch (error) {
      console.error("Error uploading video:", error);
    }
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <div className="mb-4">
        {!recordedVideoUrl && (
          <ReactMediaRecorder
            video
            onStop={handleStopRecording}
            render={({
              startRecording,
              stopRecording,
              mediaBlobUrl,
              previewStream,
            }) => (
              <>
                <VideoPreview stream={previewStream} />
                <button
                  onClick={() => {
                    if (mediaStream) {
                      stopRecording();
                      setMediaStream(null);
                    } else {
                      startRecording();
                      setMediaStream(previewStream);
                    }
                  }}
                  className={`px-4 py-2 rounded-lg text-white ${
                    mediaStream
                      ? "bg-red-500 hover:bg-red-600"
                      : "bg-green-500 hover:bg-green-600"
                  }`}
                >
                  {mediaStream ? "Stop Recording" : "Start Recording"}
                </button>
              </>
            )}
          />
        )}
        {recordedVideoUrl && (
          <video src={recordedVideoUrl} controls className="border rounded-lg">
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      {recordedVideoUrl && (
        <div className="space-x-4">
          <button
            onClick={recordAgain}
            className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
          >
            Record Again
          </button>
          <button
            onClick={moveToNextQuestion}
            className="px-4 py-2 rounded-lg text-white bg-blue-500 hover:bg-blue-600"
          >
            Next Question
          </button>
        </div>
      )}
    </div>
  );
};

export default Q1;
