import React, { useState, useRef } from "react";
import { ReactMediaRecorder } from "react-media-recorder";

const Q1 = () => {
  const [question] = useState("What is your favorite programming language?");
  const videoRef = useRef(); // Import useRef from react

  const handleStopRecording = (videoBlobUrl) => {
    console.log("Recording stopped, video URL:", videoBlobUrl);
    // You can set the video URL here if needed
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <ReactMediaRecorder
        video
        render={({ status, startRecording, stopRecording }) => (
          <>
            <div className="mb-4">
              <video
                ref={videoRef}
                controls
                className="border rounded-lg"
                autoPlay
                muted
              ></video>
            </div>
            {status === "idle" && (
              <button
                onClick={startRecording}
                className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
              >
                Start Recording
              </button>
            )}
            {status === "recording" && (
              <button
                onClick={stopRecording}
                className="px-4 py-2 rounded-lg text-white bg-red-500 hover:bg-red-600"
              >
                Stop Recording
              </button>
            )}
          </>
        )}
        onStop={(videoBlobUrl) => handleStopRecording(videoBlobUrl)}
      />
    </div>
  );
};

export default Q1;
