import React, { useState, useRef } from "react";
import { useMediaRecorder } from "react-media-recorder";

const Q1 = () => {
  const [question] = useState("What is your favorite programming language?");
  const [videoURL, setVideoURL] = useState(null);
  const { status, startRecording, stopRecording } = useMediaRecorder({
    video: true,
    onStop: (blobUrl) => setVideoURL(blobUrl),
  });
  const videoRef = useRef();

  const handleStartRecording = () => {
    startRecording();
  };

  const handleStopRecording = () => {
    stopRecording();
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-100 text-black">
      <h1 className="text-2xl font-bold mb-4">{question}</h1>
      <div className="mb-4">
        <video
          ref={videoRef}
          controls
          src={videoURL}
          className="border rounded-lg"
          autoPlay
          muted
        ></video>
      </div>
      {status === "idle" && (
        <button
          onClick={handleStartRecording}
          className="px-4 py-2 rounded-lg text-white bg-green-500 hover:bg-green-600"
        >
          Start Recording
        </button>
      )}
      {status === "recording" && (
        <button
          onClick={handleStopRecording}
          className="px-4 py-2 rounded-lg text-white bg-red-500 hover:bg-red-600"
        >
          Stop Recording
        </button>
      )}
    </div>
  );
};

export default Q1;
